-- There are three datasets in this sql file: 
-- InfoRecyclables: Items to recycles with whether it is recyclable or nonrecyclable
-- InfoRecyclableTips: Items to recycle with advices on actions for recyclables and nonrecyclables
-- InfoStats: Statistics on recycling from 2017 to 2022

-- Resources:
-- InfoRecyclables: https://www.towardszerowaste.gov.sg/recycle/what-to-recycle/
-- InfoRecyclableTips: https://www.nea.gov.sg/docs/default-source/our-services/waste-management/list-of-items-that-are-recyclable-and-not.pdf
-- InfoStats: https://www.nea.gov.sg/our-services/waste-management/waste-statistics-and-overall-recycling


--
-- Host: MariaDB
-- Database: bloobin
--

-- --------------------------------------------------------

--
-- Table structure for table InfoRecyclables
--

CREATE TABLE IF NOT EXISTS InfoRecyclables (
  ItemID INT(3) PRIMARY KEY NOT NULL,
  ItemType VARCHAR(7) DEFAULT NULL,
  Item VARCHAR(97) DEFAULT NULL,
  Recycle ENUM('YES','NO','MAYBE','') DEFAULT NULL,
  Remarks VARCHAR(566) DEFAULT NULL,
  INDEX Idx_Type (ItemType),
  INDEX Idx_Item (Item)
);

--
-- Dumping data for table InfoRecyclables
--


-- --------------------------------------------------------

--
-- Table structure for table InfoRecyclableTips
--

CREATE TABLE IF NOT EXISTS InfoRecyclableTips (
  ItemID INT NOT NULL,
  ItemType VARCHAR(7) DEFAULT NULL,
  Item VARCHAR(97) DEFAULT NULL,
  Recyclable VARCHAR(88) DEFAULT NULL,
  NonRecyclable VARCHAR(472) DEFAULT NULL,
  INDEX Idx_Type (ItemType),
  INDEX Idx_Item (Item),
  FOREIGN KEY (ItemID) REFERENCES `InfoRecyclables` (ItemID)
);

--
-- Dumping data for table InfoRecyclableTips
--


-- --------------------------------------------------------

--
-- Table structure for table InfoStats
--

CREATE TABLE IF NOT EXISTS InfoStats (
  WasteType VARCHAR(32) DEFAULT NULL,
  TotalGenerated INT DEFAULT NULL,
  TotalRecycled INT DEFAULT NULL,
  RecyclingRate VARCHAR(5) DEFAULT NULL,
  TotalDisposed INT DEFAULT NULL,
  Year INT DEFAULT NULL,
  INDEX Idx_Waste_Type (WasteType),
  INDEX Idx_Year (Year)
);

